mapper.select:
select unique_code,oyo_id,period,hotel_name,order_no,guest_name,source,checkin,checkout,period_start_date,period_end_date, check_in_days,status,oyo_rooms,amount,payments,payment_type,ota_id,city,region,hotel_id from (
select c.unique_code, c.oyo_id, #{queryAccountPeriodDto.accountPeriod} period, C.name hotel_name, A.invoice_no order_no, CONCAT(B.last_name,B.first_name) guest_name, A.source, A.checkin, A.checkout, A.checkin period_start_date, A.checkout period_end_date, (A.checkout - A.checkin) check_in_days, A.status, A.oyo_rooms, A.amount, A.payments, A.payment_type, A.ota_id, C.city, E.name region, C.id hotel_id from bookings A,user_profiles B,hotels C,hubs D,zones E where A.guest_id = B.id and A.hotel_id = C.id and D.zone_id = E.id and C.city = D.name and C.country_id = 50 and (A.checkin >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkin <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD') and A.checkout >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkout <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD'))
AND A.invoice_no ~* #{queryAccountPeriodDto.orderNo}
AND E.name = #{queryAccountPeriodDto.region}
AND C.city = #{queryAccountPeriodDto.city}
AND C.name ~* #{queryAccountPeriodDto.hotelName}
AND C.id = #{queryAccountPeriodDto.hotelId}
union all
select c.unique_code, c.oyo_id, #{queryAccountPeriodDto.accountPeriod} period, C.name hotel_name, A.invoice_no order_no, CONCAT(B.last_name,B.first_name) guest_name, A.source, A.checkin, A.checkout, to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') period_start_date, A.checkout period_end_date, (A.checkout - to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD')) check_in_days, A.status, A.oyo_rooms, A.amount, A.payments, A.payment_type, A.ota_id, C.city, E.name region, C.id hotel_id from bookings A,user_profiles B,hotels C,hubs D,zones E where A.guest_id = B.id and A.hotel_id = C.id and D.zone_id = E.id and C.city = D.name and C.country_id = 50 and (A.checkin < to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkout >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkout <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD'))
AND A.invoice_no ~* #{queryAccountPeriodDto.orderNo}
AND E.name = #{queryAccountPeriodDto.region}
AND C.city = #{queryAccountPeriodDto.city}
AND C.name ~* #{queryAccountPeriodDto.hotelName}
AND C.id = #{queryAccountPeriodDto.hotelId}
union all
select c.unique_code, c.oyo_id, #{queryAccountPeriodDto.accountPeriod} period, C.name hotel_name, A.invoice_no order_no, CONCAT(B.last_name,B.first_name) guest_name, A.source, A.checkin, A.checkout, A.checkin period_start_date, to_date(#{queryAccountPeriodDto.nextAccountPeriodStart},'YYYY-MM-DD') period_end_date, (to_date(#{queryAccountPeriodDto.nextAccountPeriodStart},'YYYY-MM-DD') - A.checkin) check_in_days, A.status, A.oyo_rooms, A.amount, A.payments, A.payment_type, A.ota_id, C.city, E.name region, C.id hotel_id from bookings A,user_profiles B,hotels C,hubs D,zones E where A.guest_id = B.id and A.hotel_id = C.id and D.zone_id = E.id and C.city = D.name and C.country_id = 50 and (A.checkin >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkin <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD') and (A.checkout > to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD') or A.checkout is null))
AND A.invoice_no ~* #{queryAccountPeriodDto.orderNo}
AND E.name = #{queryAccountPeriodDto.region}
AND C.city = #{queryAccountPeriodDto.city}
AND C.name ~* #{queryAccountPeriodDto.hotelName}
AND C.id = #{queryAccountPeriodDto.hotelId}
) as foo ORDER BY foo.hotel_name,foo.order_no,foo.guest_name,foo.checkin desc

select oyo_id,unique_code,hotel_name,period,oyo_rooms,amount,city,region,hotel_id,check_in_days from (
select C.oyo_id, C.unique_code, C.name hotel_name, #{queryAccountPeriodDto.accountPeriod} period, A.oyo_rooms, A.amount, C.city, E.name region, C.id hotel_id, (A.checkout - A.checkin) check_in_days from bookings A,user_profiles B,hotels C,hubs D,zones E where A.guest_id = B.id and A.hotel_id = C.id and D.zone_id = E.id and C.city = D.name and C.country_id = 50 and (A.checkin >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkin <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD') and A.checkout >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkout <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD'))
AND A.invoice_no ~* #{queryAccountPeriodDto.orderNo}
AND E.name = #{queryAccountPeriodDto.region}
AND C.city = #{queryAccountPeriodDto.city}
AND C.name ~* #{queryAccountPeriodDto.hotelName}
AND C.id = #{queryAccountPeriodDto.hotelId}
union all
select C.oyo_id, C.unique_code, C.name hotel_name, #{queryAccountPeriodDto.accountPeriod} period, A.oyo_rooms, A.amount, C.city, E.name region, C.id hotel_id, (A.checkout - to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD')) check_in_days from bookings A,user_profiles B,hotels C,hubs D,zones E where A.guest_id = B.id and A.hotel_id = C.id and D.zone_id = E.id and C.city = D.name and C.country_id = 50 and (A.checkin < to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkout >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkout <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD'))
AND A.invoice_no ~* #{queryAccountPeriodDto.orderNo}
AND E.name = #{queryAccountPeriodDto.region}
AND C.city = #{queryAccountPeriodDto.city}
AND C.name ~* #{queryAccountPeriodDto.hotelName}
AND C.id = #{queryAccountPeriodDto.hotelId}
union all
select C.oyo_id, c.unique_code, C.name hotel_name, #{queryAccountPeriodDto.accountPeriod} period, A.oyo_rooms, A.amount, C.city, E.name region, C.id hotel_id, (to_date(#{queryAccountPeriodDto.nextAccountPeriodStart},'YYYY-MM-DD') - A.checkin) check_in_days from bookings A,user_profiles B,hotels C,hubs D,zones E where A.guest_id = B.id and A.hotel_id = C.id and D.zone_id = E.id and C.city = D.name and C.country_id = 50 and (A.checkin >= to_date(#{queryAccountPeriodDto.accountPeriodStart},'YYYY-MM-DD') and A.checkin <= to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD') and (A.checkout > to_date(#{queryAccountPeriodDto.accountPeriodEnd},'YYYY-MM-DD') or A.checkout is null))
AND A.invoice_no ~* #{queryAccountPeriodDto.orderNo}
AND E.name = #{queryAccountPeriodDto.region}
AND C.city = #{queryAccountPeriodDto.city}
AND C.name ~* #{queryAccountPeriodDto.hotelName}
AND C.id = #{queryAccountPeriodDto.hotelId}
) as foo ORDER BY foo.oyo_id

